String.prototype.trim=function(){return this.replace(/^\s+|\s+$/g,"")};String.prototype.toCamel=function(){return this.replace(/(\-[a-z])/g,function(a){return a.toUpperCase().replace("-","")})};String.prototype.toDash=function(){return this.replace(/([A-Z])/g,function(a){return"-"+a.toLowerCase()})};String.prototype.toUnderscore=function(){return this.replace(/([A-Z])/g,function(a){return"_"+a.toLowerCase()})};